select distinct d.codigo, d.nombre from departamentos d
inner join provincias p
on d.id_provincia = p.id_provincia 
where p.codigo = '06';

select d.codigo, d.nombre from departamentos d
                            inner join provincias p
                            on d.id_provincia = p.id_provincia;
                            
select nombre_geografico, tipo_asentamiento 
from bahra b 
where codigo_indec_provincia = '06' 
and codigo_indec_departamento = '06112'
and codigo_asentamiento <> '';                           
                           
select codigo_asentamiento , nombre_geografico, tipo_asentamiento 
from bahra b 
where codigo_indec_provincia = '06' 
and codigo_indec_departamento = '06112'
and codigo_asentamiento <> '';
--and tipo_asentamiento = 'Entidad';

select * from bahra b where codigo_asentamiento = ''

select distinct nombre_provincia from bahra b2 

select distinct codigo_indec_departamento, 
nombre_departamento 
from bahra b where codigo_indec_provincia = '50'

select distinct nombre_departamento, codigo_asentamiento, nombre_geografico 
from bahra b where codigo_indec_departamento = '66014'

select distinct codigo_indec_provincia, codigo_indec_departamento, 
nombre_departamento 
from bahra b where codigo_indec_departamento = '78021'

select codigo_asentamiento as codigo,
                nombre_geografico as nombre
                from bahra b
                where codigo_indec_provincia = '06'
                   and codigo_indec_departamento = '06539'
                   and codigo_asentamiento <> '' 
                   union 
                   select codigo, sinonimo as nombre 
                   from rnpr_sinonimos rs
                   where codigo in (select codigo_asentamiento 
                from bahra b
                where codigo_indec_provincia = '06'
                   and codigo_indec_departamento = '78021'
                   and codigo_asentamiento <> '')
                   
                   
select campo_original ,
string_agg(''''||valor_original||'''',' ,') 
from rnpr_excluidos_normalizacion ren 
group by campo_original 

